def translate_to_pig_latin(sentence):
    vowels = ''
    words = sentence.split()
    translated_words = []
    for word in words:
        if word[0].lower() in vowels:
            translated_words.append(word + 'way')
        else:
            vowel_index = 0
            for char in word:
                if char.lower() in vowels:
                    break
                vowel_index += 1
            translated_words.append(word[vowel_index:] + word[:vowel_index] + 'ay')
