def find_longest_subarray(arr, target_sum):
    """"""
    longest_subarray = []
    for i in range(len(arr)):
        curr_sum = 0
        for j in range(i, len(arr)):
            curr_sum += arr[j]
            if curr_sum < target_sum and len(arr[i:j + 1]) > len(longest_subarray):
                longest_subarray = arr[i:j + 1]
    return longest_subarray
