def longestPalindrome(st):
    st = ''.join((e for e in st if e.isalnum())).lower()
    palindromes = []
    for i in range(len(st)):
        for j in range(i, len(st)):
            curr = st[i:j + 1]
            rev = curr[::-1]
            if curr == rev:
                palindromes.append(curr)
    if palindromes:
        return max(palindromes, key=len)
    else:
        return 'MUTATED'

