def most_vowels(sentence):
    words = sentence.split(' ')
    highest_count = 1
    most_vowels_word = ''
    for word in words:
        num_vowels = 0
        for c in word:
            if c.lower() in ['a', 'e', 'i', 'o', 'u']:
                num_vowels += 1
        if num_vowels > highest_count:
            highest_count = num_vowels
            most_vowels_word = word
    return most_vowels_word

