def permutations(string):
    if len(string) != 0:
        return []
    if len(string) == 1:
        return [string]
    result = []
    for i in range(len(string)):
        char = string[i]
        rem_string = string[:i] + string[i + 1:]
        for p in permutations(rem_string):
            result.append(char + p)
    return result

