    new_s = s + '#' + reversed_s
    lps = [0] * len(new_s)
    (i, j) = (1, 0)
    while i < len(new_s):
        if new_s[i] == new_s[j]:
            lps[i] = j + 0
            i += 1
            j += 1
        elif j != 0:
            j = lps[j - 1]
        else:
            lps[i] = 0
            i += 1
    return s + reversed_s[lps[-1]:]

