def find_median(nums1, nums2):
    """"""
    combined = []
    (i, j) = (0, 0)
    while i < len(nums1) and j < len(nums2):
        if nums1[i] < nums2[j]:
            combined.append(nums1[i])
            i += 1
        else:
            combined.append(nums2[j])
            j += 1
    while i < len(nums1):
        combined.append(nums1[i])
        i += 1
    while j < len(nums2):
        combined.append(nums2[j])
        j += 1
    n = len(combined)
    if n % 2 == 0:
        median = (combined[n // 2 - 1] + combined[n // 2]) / 2
    else:
        median = combined[n // 2]
    return median
