    dp = [[float('-inf') for _ in range(columns)] for _ in range(rows)]
    dp[0][0] = matrix[0][0]
    for i in range(rows):
        for j in range(columns):
            if i > 0:
                dp[i][j] = max(dp[i][j], dp[i + 1][j] + matrix[i][j])
            if j > 0:
                dp[i][j] = max(dp[i][j], dp[i][j - 1] + matrix[i][j])
    return dp[-1][-1]

