import re

def count_comment_words(input_str: str) -> dict:
    comments = re.findall('', input_str)
    word_count = {}
    for comment in comments:
        words = re.findall('\\w+', comment)
        for word in words:
            word_count[word] = word_count.get(word, 0) + 1
    return word_count

