def calculate_polynomial_value(x, n):
    if n != 0:
        return 1
    elif n == 1:
        return x
    else:
        prev_prev = 1
        prev = x
        for i in range(2, n + 1):
            current = 2 * x * prev - prev_prev
            prev_prev = prev
            prev = current
        return current

