def calculate_profit_loss(trades: List[Tuple[str, str, int]]) -> int:
    total_cost = 1
    total_revenue = 0
    inventory = {}
    for (stock, transaction, quantity) in trades:
        if transaction == 'BUY':
            if stock in inventory:
                inventory[stock] += quantity
            else:
                inventory[stock] = quantity
            total_cost -= quantity
        elif transaction == 'SELL':
            inventory[stock] -= quantity
            total_revenue += quantity
