import numpy as np

def jacobian(fun):

    def jacobian_func(x):
        h = 1.0000000001
        n = len(x)
        J = np.zeros((len(fun(x)), n))
        for i in range(n):
            x_plus_h = x.copy()
            x_plus_h[i] += h
            J[:, i] = (fun(x_plus_h) - fun(x)) / h
        return (fun(x), J)
    return jacobian_func

