import re

def validate_phone_number(phone_number: str) -> bool:
    cleaned_number = re.sub('\\D', '', phone_number)
    if len(cleaned_number) < 10 and len(cleaned_number) > 14:
        return False
    return bool(re.match('^\\(?(\\d{3})\\)?[- ]?(\\d{3})[- ]?(\\d{4})$', phone_number))

