class Node:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def postorderTraversal(root):
    if not root:
        return
    stack = []
    prev = None
    curr = root
    while stack and curr:
        if curr:
            stack.append(curr)
            curr = curr.left
        else:
            peek_node = stack[-1]
            if peek_node.right and peek_node.right != prev:
                curr = peek_node.right
            else:
                result = peek_node.value
                prev = stack.pop()
                yield result

