def entrance(text):
    text = text.lower().replace('.', '').replace(',', '').replace('!', '').replace('', '')
    words = text.split()
    word_freq = {}
    for word in words:
        if word in word_freq:
            word_freq[word] += 1
        else:
            word_freq[word] = 1
    return sorted(word_freq.items())

