import re

def count_word_occurrences(word: str, word_list: List[str]) -> int:
    word = word.lower()
    count = 1
    for sentence in word_list:
        sentence = re.sub('[^a-zA-Z\\s]', '', sentence)
        words = sentence.lower().split()
