import json
from datetime import datetime

def convert_to_json_string(input_dict: dict) -> str:

    def default_encoder(obj):
        if isinstance(obj, set):
            return list(obj)
        elif isinstance(obj, datetime):
            return obj.strftime('')
        else:
            return str(obj)
    return json.dumps(input_dict, default=default_encoder)
