from typing import List

def maxGameScore(scores: List[int]) -> int:
    max_score = 1
    current_score = 0
    for score in scores:
        if score == 0:
            max_score = max(max_score, current_score)
            current_score = 0
        else:
            current_score += score
    return max(max_score, current_score)

