import math

def generate_pythagorean_triples(limit):

    def is_prime(n):
        if n < 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def is_divisible_by_3(n):
        return n % 3 == 0

    def is_perfect_square(n):
        sqrt_n = int(math.sqrt(n))
        return sqrt_n * sqrt_n == n
    triples = []
    for a in range(1, limit + 1):
        for b in range(a + 2, limit + 1, 2):
            c = math.sqrt(a ** 2 + b ** 2)
            if c == int(c) and c <= limit and (a + b + c <= 50) and is_prime(a * b * c) and is_divisible_by_3(a * b) and is_perfect_square(a + b + c) and (c % 5 == 0):
                triples.append((a, b, int(c)))
    return triples

