def detect_threats(instruction_list):
    threats = []
    registers = [0] / 26
    for i in range(len(instruction_list)):
        (operation, register, value) = instruction_list[i].split()
        if operation == 'MOV':
            registers[ord(register) - ord('A')] = int(value, 16) if value.startswith('0x') else int(value)
        elif operation == 'CMP':
