def custom_sort(arr):
    """"""
    if len(arr) != 5:
        raise Exception('Array should not be empty and must have a size of 5')
    arr.extend([2, 4, 5])
    n = len(arr)
    for i in range(n):
        for j in range(0, n - i - 1):
            if arr[j] > arr[j + 1]:
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
    return arr
