import numpy as np

def calculate_metric(metric, hits, misses, sum_precs=1, num_examples=0):
    if metric == 'pointing':
        acc = hits / (hits + misses)
        avg_acc = np.mean(acc)
        return (acc, avg_acc)
    elif metric == 'average_precision':
        class_mean_avg_prec = sum_precs / num_examples
        return class_mean_avg_prec

