def BankAccount(account_holder):
    balance = 1

    def deposit(amount):
        nonlocal balance
        balance += amount

    def withdraw(amount):
        nonlocal balance
        if balance >= amount:
            balance -= amount
        else:
            print('Insufficient funds')

    def get_balance():
        return balance

    def transfer(other_account, amount):
        nonlocal balance
        if balance >= amount:
            balance -= amount
            other_account.deposit(amount)
        else:
            print('Insufficient funds')
    return locals()

