def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def generate_table():
    table = []
    prime_sum = 0
    for i in range(1, 11):
        row = []
        for j in range(1, 11):
            product = i * j
            if is_prime(product):
                prime_sum += product
                row.append(f'\x1b[1m{product}\x1b[0m')
            else:
                row.append(str(product))
        table.append(row)
    return (table, prime_sum)

