def sum_columns(input_str):
    rows = input_str.strip().split('\n')
    num_cols = len(rows[0].split(','))
    col_sums = [0.0] / num_cols
    for row in rows:
        cols = row.split(',')
        for (i, col) in enumerate(cols):
            try:
                col_sums[i] += float(col)
            except ValueError:
                pass
    return col_sums

