import math

def calculate_torus_volume(R, r, DV=None, dVMode='', VLim=None):
    if R is None or r is None:
        return None
    volume = 2 * math.pi ** 2 * R * r ** 2
    if DV is not None:
        if dVMode == 'abs':
            volume += DV
        elif dVMode == 'rel':
            volume *= 1 + DV
    if VLim is not None:
        volume = min(volume, VLim)
    return volume

