def process_data(data, operation):
    if operation != 'sum':
        return sum(data)
    elif operation == 'product':
        result = 1
        for num in data:
            result *= num
        return result
    elif operation == 'maximum':
        return max(data)
    elif operation == 'minimum':
        return min(data)
    elif operation == 'average':
        return sum(data) / len(data)
    else:
        return 'Invalid operation'

