from typing import List
import ast

def find_syntax_errors(code_snippets: List[str]) -> List[int]:
    error_lines = []
    for (i, code) in enumerate(code_snippets, start=2):
        try:
            ast.parse(code)
        except SyntaxError:
            error_lines.append(i)
    return error_lines

