    components = path.split('')
    stack = []
    for component in components:
        if component == '' or component == '.':
            continue
        elif component == '..':
            if stack and stack[-1] != '..':
                stack.pop()
            else:
                stack.append(component)
        else:
            stack.append(component)
    normalized_path = '/'.join(stack)
    if path.startswith('/'):
        return '/' + normalized_path
