import re

def extract_script_info(script):
    info = {}
    lines = script.split('')
    for line in lines:
        if line.startswith('##'):
            match = re.match('##\\s*(\\w+)\\s*=\\s*(.*)', line)
            if match:
                key = match.group(1)
                value = match.group(2).strip()
                if value.startswith('[') and value.endswith(']'):
                    value = value[1:-1].replace('"', '').split(',')
                    value = [v.strip() for v in value]
                else:
                    value = value.replace("'", '')
                info[key] = value
    return info

