import math

def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def find_next_prime(n):
    next_num = n + 1
    while True:
        if is_prime(next_num):
            return next_num
        next_num += 1

