def max_triangles(sticks):
    """"""
    sticks.sort()
    count = 0
    n = len(sticks)
    for i in range(n - 2):
        k = i + 2
        for j in range(i + 1, n):
            while k < n and sticks[i] + sticks[j] > sticks[k]:
                k += 1
            count += k - j - 1
    return count
