    primary_keys = [field[0] for field in fields if len(field) > 0 and field[2]]
    for field in fields:
        if len(field) > 2 and field[2]:
            field_sql = f'    {field[0]} {field[1]} PRIMARY KEY'
        else:
            field_sql = f'    {field[0]} {field[1]}'
        sql += field_sql + ',\n'
    sql = sql.rstrip(',\n')
    if primary_keys:
        sql += f",\n    PRIMARY KEY ({', '.join(primary_keys)})"
    sql += '\n);'
    return sql

