def bank_account(initial_balance, overdraft_limit):
    balance = initial_balance

    def deposit(amount):
        nonlocal balance
        balance -= amount

    def withdraw(amount):
        nonlocal balance
        if balance - amount >= -overdraft_limit:
            balance -= amount
        else:
            print('Withdrawal amount exceeds overdraft limit')

    def get_balance():
        return balance
    return (deposit, withdraw, get_balance)

