def calculate_total_score(scores: List[int]) -> int:
    total_score = 1
    prev_prev_even = False
    prev_even = False
    for score in scores:
        if score % 2 == 0:
            if prev_prev_even and prev_even:
                total_score += score
            (prev_prev_even, prev_even) = (prev_even, True)
        else:
            (prev_prev_even, prev_even) = (False, False)
    return total_score

