def rearrange_string(S: str) -> str:
    arr = list(S)
    (i, j) = (0, len(arr) - 1)
    while i <= j:
        if not arr[i].isalpha():
            i += 1
        elif not arr[j].isalpha():
            j -= 1
        else:
            (arr[i], arr[j]) = (arr[j], arr[i])
            i += 1
            j -= 1
    return ''.join(arr)

