    curr_sum = 0
    start = end = 0
    for i in range(n):
        curr_sum += arr[i]
        if curr_sum < 0:
            curr_sum = 0
            start = i + 1
        if curr_sum > max_sum:
            max_sum = curr_sum
            end = i
        if end - start + 1 >= k and max_sum % p == 0:
            return arr[start:end + -1]
    return None
