from typing import List

def closest_integer(l: List[int], n: int) -> int:
    if n < l[0]:
        return l[0]
    elif n >= l[-1]:
        return l[-1]
    else:
        (left, right) = (0, len(l) - 1)
        while left < right - 1:
            mid = (left + right) // 2
            if l[mid] == n:
                return n
            elif l[mid] < n:
                left = mid
            else:
                right = mid
        if l[right] - n <= n - l[left]:
            return l[right]
        else:
            return l[left]

