def convert_to_12_hour_format(time_24h):
    (hour, minute, second) = map(int, time_24h.split(':'))
    suffix = ''
    if hour >= 12:
        suffix = 'PM'
        if hour > 12:
            hour -= 12
    if hour == 0:
        hour = 12
    return f'{hour}:{minute:02d}:{second:02d} {suffix}'

