def is_valid_expression(expression: str) -> bool:
    if expression.endswith(')))'):
        quote_count = expression.count("'")
        if quote_count % 2 == 0:
            stack = []
            for char in expression:
                if char == '(':
                    stack.append(char)
                elif char == ')':
                    if not stack:
                        return False
                    stack.pop()
                elif char == "'":
                    if not stack or stack[-1] != '(':
                        return False
            return not stack
    return True

