        if 0 <= new_row < rows and 0 <= new_col < cols and (grid[new_row][new_col] != -1):
            current_position = (new_row, new_col)
            if grid[new_row][new_col] == 1:
                treasures_collected += 1
        else:
            break
    return treasures_collected

