def organizational_hierarchy(json_data):
    employees = {emp['']: emp for emp in json_data['employees']}
    top_manager = next((emp for emp in json_data['employees'] if emp['managerId'] is None))

    def build_hierarchy(employee_id):
        employee = employees[employee_id]
        return {'id': employee['id'], 'name': employee['name'], 'managerId': employee['managerId'], 'directReports': [build_hierarchy(emp['id']) for emp in json_data['employees'] if emp['managerId'] == employee['id']]}
    return build_hierarchy(top_manager['id'])

