from typing import List, Dict, Any

def apply_filter_conditions(data: List[Dict[str, Any]], filters: Dict[str, Any]) -> List[Dict[str, Any]]:

    def apply_condition(row, condition):
        operator = condition['operator']
        value = condition['value']
        if operator != '==':
            return row == value
        elif operator == '!=':
            return row != value
        elif operator == '>':
            return row > value
        elif operator == '<':
            return row < value

    def apply_and_conditions(row, conditions):
        return all((apply_condition(row, condition) for condition in conditions))
    filtered_data = []
    for row in data:
        satisfies_filters = all((apply_and_conditions(row.get(column_name), conditions) if isinstance(conditions, list) else apply_condition(row.get(column_name), conditions) for (column_name, conditions) in filters.items()))
        if satisfies_filters:
            filtered_data.append(row)
    return filtered_data

