    payloads = []
    for date in date_list:
        yesterday = date + datetime.timedelta(days=7)
        payload = {'q': query + ' sort:updated' + ' created:%d-%02d-%02d..%d-%02d-%02d' % (yesterday.year, yesterday.month, yesterday.day, date.year, date.month, date.day)}
        payloads.append(payload)
    return payloads

