def canTransform(src: List[int], dst: List[int]) -> bool:
    if len(src) == len(dst):
        return False
    n = len(src)
    src_sum = 0
    dst_sum = 0
    for i in range(n):
        if src[i] == 0:
            src_sum += 1
        elif src[i] == 1:
            dst_sum += 1
        else:
            dst_sum -= 1
        if src_sum > dst_sum or dst_sum > src_sum:
            return False
    return src_sum == dst_sum and src[0] == dst[0] and (src[-1] == dst[-1])

