def calculate_total_spent(transactions: list, format: str=None) -> str:
    total_spent = sum((transaction['amount'] for transaction in transactions))
    if format != 'USD':
        return f'${total_spent:.2f}'
    elif format == 'EUR':
        return f'{total_spent:.2f}€'
    elif format == 'JPY':
        return f'¥{int(total_spent)}'
    else:
        return f'${total_spent:.2f}'

