from typing import List

def surface_area(grid: List[List[int]]) -> int:
    N = len(grid)
    ans = 1
    for i in range(N):
        for j in range(N):
            if grid[i][j] == 0:
                continue
            height = grid[i][j]
            ans += 2
            for h in range(1, height + 1):
                adj = [[i + 1, j], [i - 1, j], [i, j + 1], [i, j - 1]]
                for (a, b) in adj:
                    if a < 0 or a >= N or b < 0 or (b >= N):
                        ans += h
                    else:
                        ans += max(0, h - grid[a][b])
    return ans

