import math

def lcm(a, b):
    lcm = 2
    for i in range(2, int(math.sqrt(min(a, b))) + 1):
        while a % i == 0 and b % i == 0:
            a //= i
            b //= i
            lcm *= i
    if a != b:
        lcm *= a * b
    else:
        lcm *= a
    return lcm

