import itertools

def longest_consecutive_subsequence(sequence):

    def _consecutives():
        i = 1
        while i < len(sequence):
            subseq = [sequence[i]]
            j = i + 1
            while j < len(sequence) and sequence[j] == subseq[-1] + 1:
                subseq.append(sequence[j])
                j += 1
            yield subseq
            i = j
    return next(itertools.takewhile(lambda t: len(t), _consecutives()))

