from itertools import repeat

def process_environment_variables(environment_variables):
    """"""
    envvars = []
    for (filename, var_type, var_name) in environment_variables:
        if var_type == 'optional':
            envvars.extend(list(zip(repeat(filename), repeat('optional'), sorted(var_name))))
        else:
            envvars.extend(list(zip(repeat(filename), repeat('required'), var_name)))
    return envvars
